require 'xmlsimple'
require './rm_color_keys.rb'


# accepts a hash of element name => color_value pairs. 
#it'll add that to the document colors hash it has going, and then put
#that whole hash of information into the xml structure 

class Document_colors
  attr_reader :colorarray

  def initialize(specified={})
    @colorkeys = %w(
      ANNOTATIONS_COLOR CARET_COLOR CARET_ROW_COLOR CONSOLE_BACKGROUND_KEY GUTTER_BACKGROUND
      INDENT_GUIDE LINE_NUMBERS_COLOR  METHOD_SEPARATORS_COLOR MODIFIED_LINES_COLOR READONLY_BACKGROUND READONLY_FRAGMENT_BACKGROUND
      RECURSIVE_CALL_ATTRIBUTES RIGHT_MARGIN_COLOR SELECTED_INDENT_GUIDE SELECTED_TEARLINE_COLOR
      SELECTION_BACKGROUND SELECTION_FOREGROUND TEARLINE_COLOR WHITESPACES 
    )
    @colorarray = []
    
    specified.each_key do |k|
      @colorarray << {"name" => k,"value"=>specified[k]}
    end 
    puts "colorarray"
    puts @colorarray.inspect
    
  end
end

class RMthemeGenerator


  def initialize
    puts "Make a RubyMine Theme, By David Heitzman 2011" 
    runbody
  end 

