  def set_doc_colors(doc_cols)
    #Hand this the Document_colors object you just updated with the doc's color info
    @xmlstruct["colors"][0]["options"] = doc_cols.colorarray if doc_cols 
  end
  
#make the filename
  def runbody
    @fn = []
    "szalFQ198".each_char {|c| @fn << c}
    @fn.shuffle!
    @filename = "" 
    @fn.each {|c| @filename << c }
    @themename = "rm_theme_"+@filename
    @filename = "rm_theme_"+@filename+".xml"
    
    puts @filename
    
    @file_out = File.new(@filename, "w+")
    
